package dhbw.ipoat.encrypt;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class EncryptionTest {

    @Test
    public void doEncryption() {

        //Arrange
        String teststring = "ABC";
        String result;

        //Act
        result = Encryption.doEncryption(teststring, 1);


        //Assert
        assertEquals("", result);

    }

    @Test
    public void doEncryptionWithAllCharacters() {
        //Arrange

        StringBuilder teststring = new StringBuilder();
        for (int i = 0; i < 128; i++) {
            teststring.append((char)i);
        }

        String result;

        //Act
        result = Encryption.doEncryption(teststring.toString(),42);

        //Assert
        assertEquals(result, "");

    }

    @Test
    public void doDecrypting() {
        String teststring = "";
        String result;

        StringBuilder output = new StringBuilder();
        for (int i = 0; i < 128; i++) {
            output.append((char)i);
        }

        result = Encryption.doDecrypting(teststring);

        assertEquals(output.toString(), result);
    }



}